function startLoadingAnimation(){$("#songUploadAnim").removeClass("d-none")}$(document).ready(function(){$("a.active").removeClass("active");$('a[href="'+location.pathname+'"]').closest(".list-group-item").addClass("active");$("#songForm").submit(function(){$("#songForm").valid()&&startLoadingAnimation()});$("#songUpload").change(function(){if(this.files.length>0){var t=$("#songUpload")[0].files[0],n=t.name.split(".").pop();n==="wav"||n==="mp3"?$(this).addClass("file-valid"):$(this).removeClass("file-valid")}else $(this).removeClass("file-valid")})});