function uploadFiles(n){for(var u=document.getElementById(n),i=u.files,r=new FormData,t=0;t!==i.length;t++)r.append("files",i[t]);$.ajax({url:"../api/files/upload",data:r,processData:!1,contentType:!1,type:"POST",xhr:function(){var n=new window.XMLHttpRequest;return n.upload.addEventListener("progress",function(n){if(n.lengthComputable){var t=Math.round(n.loaded/n.total*100);$(".progress-bar").css("width",t+"%").attr("aria-valuenow",t);$(".progress-bar").html(t+"%")}},!1),n},success:function(){$("#progress").hide();$("#upload-status").show()}})}$(document).ready(function(){$("a.active").removeClass("active");$('a[href="'+location.pathname+'"]').closest(".list-group-item").addClass("active")});